def construct_subset_tuple(N):
    k = 1
    n = 0
    while n < N:
        n += k
        k += 1
    if n != N:
        return 'No'
    k += 1
    subsets = [[] for _ in range(k)]
    v = 1
    for i in range(k - 1):
        for j in range(i + 1, k):
            subsets[i].append(v)
            subsets[j].append(v)
            v += 1
    return ('Yes', k, subsets)

