def find_next_tolerable_string(n, p, s):

    def is_tolerable(s):
        for i in range(len(s) - 1):
            if s[i] == s[i + 1]:
                return False
            if i < len(s) - 2 and s[i] == s[i + 2]:
                return False
        return True

    def next_char(c, p):
        return chr((ord(c) - ord('a') + 1) % p + ord('a'))
    s = list(s)
    for i in range(n - 1, -1, -1):
        original_char = s[i]
        while True:
            s[i] = next_char(s[i], p)
            if s[i] != original_char and is_tolerable(s[:i + 1]):
                break
            if s[i] == original_char:
                break
        if s[i] != original_char:
            for j in range(i + 1, n):
                for k in range(p):
                    s[j] = chr(ord('a') + k)
                    if is_tolerable(s[:j + 1]):
                        break
            return ''.join(s)
    return ''

