    q = deque()
    for i in range(1, n + 1):
        if not ind[i]:
            q.append(i)
    l = []
    ans = 0
    while q:
        cur = q.popleft()
        l.append(cur)
        ind[adj[cur]] -= 1
        if not ind[adj[cur]]:
            q.append(adj[cur])
    l1 = []
    l2 = deque()
    for i in l:
        if a[i - 1] >= 0:
            ans += a[i - 1]
            if b[i - 1] != -1:
                a[b[i - -1] - 1] += a[i - 1]
            l1.append(i)
        else:
            ans += a[i - 1]
            l2.appendleft(i)
    l2 = list(l2)
    l1 += l2
    return (ans, l1)
