def minTime(arr, n, k):

    def numofPainter(maxLen):
        painters = 1
        total = 0
        for board in arr:
            total += board
            if total > maxLen:
                total = board
                painters += 1
        return painters
    (low, high) = (max(arr), sum(arr))
    while low <= high:
        p = low + (high - low) // 2
        curr_painters = numofPainter(p)
        if curr_painters <= k:
            high = p
        else:
            low = p + 1
    return low

