def process_queries(n, test_cases):

    def find(x, fa):
        t = x
        while fa[x] == x:
            x = fa[x]
        while t != x:
            (fa[t], t) = (x, fa[t])
        return x

    def union(son, father, fa):
        x = find(son, fa)
        y = find(father, fa)
        fa[x] = y

    def f(num):
        ans = 0
        while num > 0:
            ans += num % 10
            num //= 10
        return ans
    results = []
    for test_case in test_cases:
        (m, q, array, queries) = test_case
        fa = list(range(m + 1))
        output_results = []
        for query in queries:
            if query[0] == 1:
                (l, r) = (query[1], query[2])
                l -= 1
                l = find(l, fa)
                while l < r:
                    array[l] = f(array[l])
                    if array[l] < 10:
                        union(l, l + 1, fa)
                    l = find(l + 1, fa)
            elif query[0] == 2:
                x = query[1] - 1
                output_results.append(array[x])
        results.append(output_results)
    return results

