def max_fruits(arr, N, M):
    if M >= N:
        return sum(arr)
    max_f = sum(arr[:M])
    l = 0
    cur_sum = max_f
    while l < N:
        cur_sum -= arr[l]
        if l + M < N:
            cur_sum += arr[l + M]
        else:
            cur_sum += arr[(l + M) % N]
        max_f = max(cur_sum, max_f)
        l += 1
    return max_f

