def calculate_tie_probability(t: int, w: int, b: int) -> tuple:
    """"""

    def gcd(a, b):
        if b == 0:
            return a
        return gcd(b, a % b)

    def lcm(a, b):
        return a * b // gcd(a, b)
    lc = lcm(w, b)
    mn = min(w, b)
    ans = mn * (t // lc + 1) - 1
    val = t // lc * lc + mn - 1
    if t - val < 0:
        ans += t - val
    g = gcd(ans, t)
    ans //= g
    t //= g
    return (ans, t)
