def determine_first_to_run_out_of_candies(a: int, b: int) -> str:
    step = 2
    while a >= 0 and b >= 0:
        if step % 2 == 1:
            a -= step
        else:
            b -= step
        step += 1
    return 'Vladik' if a < 0 else 'Valera'

