def min_subarrays_to_divide(A: list) -> int:
    """"""
    N = len(A)
    if N == 0:
        return 0
    sgn = 0
    answer = 1
    for i in range(N - 1):
        cur = A[i + 1] - A[i]
        if cur * sgn < 0:
            answer += 1
            sgn = 0
            continue
        if cur != 0:
            sgn = cur
    return answer
