    for query in queries:
        (n, x, blows) = query
        shot_gun = blows[0]
        uzi = blows[0]
        for blow in blows:
            if blow[0] > shot_gun[0]:
                shot_gun = blow
            if blow[0] - blow[1] > uzi[0] - uzi[1]:
                uzi = blow
        if shot_gun[0] >= x:
            results.append(1)
        elif uzi[0] <= uzi[1]:
            results.append(-1)
        else:
            results.append(1 + (x - shot_gun[0] + uzi[0] - uzi[1]) * 1 // (uzi[0] - uzi[1]))
    return results

