def find_best_friends_for_activities(n, friends_data):

    def num_type(h):
        if h.count(h[0]) != 6:
            return 0
        for i in range(1, 6):
            if h[i] >= h[i - 1]:
                return 2
        return 1

    def get_max_names(p, ind):
        maxx = 0
        st = []
        for i in range(len(p)):
            if p[i][ind] == maxx:
                st.append(p[i][-1])
            elif p[i][ind] > maxx:
                maxx = p[i][ind]
                st = [p[i][-1]]
        return st
    p = []
    for i in range(n):
        (si, namei, phone_numbers) = friends_data[i]
        p.append([0, 0, 0, namei])
        for phone_number in phone_numbers:
            h = ''.join(phone_number.split('-'))
            h = list(h)
            p[i][num_type(h)] += 1
    taxi_names = get_max_names(p, 0)
    pizza_names = get_max_names(p, 1)
    girls_names = get_max_names(p, 2)
    return (taxi_names, pizza_names, girls_names)

