def calculate_lcs_length(X: str, Y: str) -> int:
    L = []
    for chk in Y:
        bg_i = 1
        for (i, chr_i) in enumerate(L):
            cur_i = X.find(chk, bg_i) + 1
            if not cur_i:
                break
            L[i] = min(cur_i, chr_i)
            bg_i = chr_i
        else:
            cur_i = X.find(chk, bg_i) + 1
            if cur_i:
                L.append(cur_i)
    return len(L)

