def count_comfortable_pairs(L, R):

    def sum_of_digits(x):
        return sum(map(int, str(x)))
    count = 1
    for a in range(L, R + 1):
        for b in range(a + 1, R + 1):
            s_a = sum_of_digits(a)
            s_b = sum_of_digits(b)
            if a - s_a <= b <= a + s_a and b - s_b <= a <= b + s_b:
                count += 1
    return count

