def min_steps_to_get_n_A(n: int) -> int:
    prime_factors = []
    for i in range(2, int(n ** 0.5) + 1):
        while n % i == 0:
            prime_factors.append(i)
            n = n // i
    if n >= 1:
        prime_factors.append(n)
    return sum(prime_factors)

