def minimal_permutation(n, a):
    for i in range(n):
        a[i] = a[i] - 1
    occ = {}
    for i in range(n):
        if a[i] not in occ:
            occ[a[i]] = 1
        else:
            occ[a[i]] += 1
    missing = []
    for i in range(n):
        if i not in occ:
            missing.append(i)
    act_missing = 1
    left = [1] * n
    for pos in range(n):
        if a[pos] in occ and occ[a[pos]] > left[a[pos]]:
            if act_missing < len(missing) and (missing[act_missing] < a[pos] or left[a[pos]] == 0):
                occ[a[pos]] -= 1
                a[pos] = missing[act_missing]
                act_missing += 1
            else:
                left[a[pos]] -= 1
                occ[a[pos]] -= 1
    for i in range(n):
        a[i] = a[i] + 1
    return (len(missing), a)

