def calculate_character_frequency_in_pyramid(n, s, queries):
    alph = ''
    arr = [0] * 26
    pref = []
    for i in range(len(s)):
        for j in range(26):
            if alph[j] == s[i]:
                arr[j] += 1
                break
        pref.append(arr[:])
    results = []
    for (r, c) in queries:
        r = int(r)
        ind = alph.index(c)
        prev = ((r - 1) ** 2 + r - 1) // 2
        done = prev % len(s)
        ans = 0
        rem = (len(s) - done) % len(s)
        if r <= rem:
            if done > 0:
                results.append(pref[done + r - 1][ind] - pref[done - 1][ind])
            else:
                results.append(pref[done + r - 1][ind])
            continue
        if rem != 0:
            if done > 0:
                ans += pref[-1][ind] - pref[done - 1][ind]
            else:
                ans += pref[-1][ind]
            r -= rem
        ans += pref[-1][ind] * (r // len(s))
        r %= len(s)
        if r != 0:
            ans += pref[r - 1][ind]
        results.append(ans)
    return results

