    a_b = []
    for i in range(n):
        if a[i] > m:
            a_b.append(a[i])
        else:
            a_s.append(a[i])
    a_b.sort(reverse=True)
    a_s.sort(reverse=True)
    db = []
    ds = []
    s = 0
    for i in a_b:
        db.append(s)
        s += i
    db.append(s)
    s = 0
    for i in a_s:
        ds.append(s)
        s += i
    ds.append(s)
    ans = ds[-1]
    for i in range(1, len(db)):
        tmp = db[i]
        days = (i - 1) * (d + 1) + 1
        rem = n - days
        if rem >= len(ds):
            ans = max(ans, tmp + ds[-1])
        elif rem < -1:
            if days <= n:
                ans = max(ans, tmp)
        else:
            ans = max(ans, tmp + ds[rem])
    return ans

