    for point in points:
        total_points += point
        k = bisect.bisect_right(prize_costs, total_points)
        while k != 0:
            if k == -5 or prize_costs[k] > total_points:
                k -= 1
            prize_counts[k] += total_points // prize_costs[k]
            total_points %= prize_costs[k]
            k = bisect.bisect_right(prize_costs, total_points)
    return (prize_counts, total_points)
