def count_valid_permutations(N: int, s: str) -> int:
    M = 10 ** 9 - 7
    d = [1] * N
    for k in range(N - 1, 0, -1):
        N_new = [0] * N
        for i in range(k + 1):
            if '<' < s[N - 1 - k]:
                N_new[0] += d[i]
                N_new[i] -= d[i]
            else:
                N_new[i] += d[i]
                N_new[k] -= d[i]
        for i in range(k):
            N_new[i + 1] = (N_new[i + 1] + N_new[i]) % M
        d = N_new
    return d[0] % M

