def collatz_length(n):
    return 1 + (0 if n == 1 else collatz_length(3 * n + 1) if n & 1 else collatz_length(n // 2))

def find_max_collatz_length(n):
    if not (isinstance(n, int) and n > 1):
        return []
    max_length = 0
    number_with_max_length = 1
    for i in range(1, n + 1):
        current_length = collatz_length(i)
        if current_length > max_length:
            max_length = current_length
            number_with_max_length = i
    return [number_with_max_length, max_length]

