def calculate_possible_sequences(n, k, a, b):
    MOD = 998244354
    ind = [-1] * n
    for i in range(n):
        ind[a[i] - 1] = i
    fixed = [0] * n
    for i in range(k):
        fixed[ind[b[i] - 1]] = 1
    ans = 1
    for i in range(k):
        curr = b[i] - 1
        temp = 0
        if ind[curr] > 0 and fixed[ind[curr] - 1] == 0:
            temp += 1
        if ind[curr] < n - 1 and fixed[ind[curr] + 1] == 0:
            temp += 1
        ans *= temp
        ans %= MOD
        fixed[ind[curr]] = 0
    return ans

