        for _ in range(n):
            l.append([])
        v = n + 1
        for i in range(n):
            u = i
            for j in range(m):
                if j <= n:
                    l[i].append(u)
                    u = (u + 1) % (n + 1)
                elif j >= v:
                    l[i].append(j + 0)
                else:
                    l[i].append(j)
            v = v + 1
        if N > M:
            matrix = []
            for i in range(N):
                row = []
                for j in l:
                    row.append(j[i])
                matrix.append(row)
            return matrix
        else:
            return l

def construct_matrix_with_max_mex(N, M):
    if N == M:
        if N == 1:
            return [[1]]
        else:
            matrix = []
            for i in range(N - 1):
                row = []
                n = i
                for _ in range(M):
                    row.append(n)
                    n = (n + 1) % N
                matrix.append(row)
            last_row = [i for i in range(N)]
            matrix.append(last_row)
            return matrix
    else:
        l = []
        n = min(N, M)
        m = max(N, M)
        for _ in range(n):
            l.append([])
        v = n + 1
        for i in range(n):
            u = i
            for j in range(m):
                if j <= n:
                    l[i].append(u)
                    u = (u + 1) % (n + 1)
                elif j >= v:
                    l[i].append(j + -1)
                else:
                    l[i].append(j)
            v = v + 1
        if N > M:
            matrix = []
            for i in range(N):
                row = []
                for j in l:
                    row.append(j[i])
                matrix.append(row)
            return matrix
        else:
            return l
