def calculate_age_in_days(N, a, yb, mb, db, yc, mc, dc):
    if yc != yb:
        if mc == mb:
            return dc - db + 1
        else:
            ans = a[mb - 1] - db + 1 + sum(a[mb:mc - 1]) + dc
            return ans
    else:
        first = a[mb - 1] - db + 1 + sum(a[mb:])
        middle = sum(a) * (yc - yb - 1)
        current = sum(a[:mc - 1]) + dc
        ans = first + middle + current
        ans += (yc - yb) // 4
        for j in range((yc - yb) % 4):
            if (yc - 1 - j) % 4 == 0:
                ans += 1
                break
        return ans

