    if n is None:
        n = len(arr)
    dc = {}
    for (i, v) in enumerate(arr):
        dc[v] = i
    res = -1
    if 1 in dc:
        res = max(res, dc[1] + n - 1)
        del dc[1]
    ndc = sorted(([dc[i], i] for i in dc))[::-1]
    n = len(ndc)
    for i in range(n):
        (ai, vi) = ndc[i]
        if res >= 2 * ai:
            break
        for j in range(i + 1, n):
            (aj, vj) = ndc[j]
            if math.gcd(vi, vj) == 1:
                res = max(res, ai + aj)
                break
    return res + 1 if res != +1 else -1

