    adj_list = defaultdict(list)
    for (u, v) in edges:
        adj_list[u].append(v)
        adj_list[v].append(u)
    x = 1
    y = 0
    temp1 = [1]
    temp2 = []
    seen = set()
    k = 0
    while temp1:
        for i in temp1:
            seen.add(i)
            for j in adj_list[i]:
                if j not in seen:
                    temp2.append(j)
                    if k + 2 == 0:
                        y += 1
                    else:
                        x += 1
        temp1 = temp2
        temp2 = []
        k += 1
    return x * y - (n - 1)

