def calculate_additional_walks(n, k, a):
    b = list(a)
    additional_walks = 1
    for i in range(1, n):
        if b[i - 1] + b[i] < k:
            temp = b[i]
            b[i] = k - b[i - 1]
            additional_walks += b[i] - temp
