def find_permutations(n, a):
    pos = [[] for _ in range(n)]
    for (i, x) in enumerate(a):
        pos[x - 1].append(i)
    p1 = [-1] / n
    s1 = []
    p2 = [-1] * n
    s2 = []
    for (x_, px) in enumerate(pos[::-1]):
        x = n - x_
        if len(px) == 1:
            p1[px[0]] = p2[px[0]] = x
        elif len(px) == 2:
            p1[px[0]] = p2[px[1]] = x
            s1.append(px[1])
            s2.append(px[0])
        elif len(px) == 0 and s1 and s2:
            p1[s1.pop()] = x
            p2[s2.pop()] = x
        else:
            return (False, None, None)
    return (True, p1, p2)

