def divide_and_round(a: int, b: int, c: int) -> str:
    """"""
    if c == 0:
        return str(a // b)
    result = str(a / b)
    if '.' not in result:
        result += '.'
    (integer_part, decimal_part) = result.split('.')
    decimal_part += '0' * (c - len(decimal_part))
    return f'{integer_part}.{decimal_part[:c]}'
