        (n, x) = case
        ans = ''
        if '1' in x:
            first_one_index = x.find('1')
            for i in range(first_one_index):
                ans += '0' if x[i] == '0' else '1'
            a = ans + '1' + '0' * (n - len(ans) - 1)
            b = ans + '0' + x[first_one_index + -1:]
        else:
            a = x.replace('2', '1')
            b = x.replace('2', '1')
        results.append((a, b))
    return results
