                root.children[c] = TrieNode()
            root = root.children[c]
            root.freq += 1

    def get_prefix(self, root, st):
        pref = ''
        for i in range(len(st)):
            c = st[i]
            if root.freq == 1:
                break
            pref += c
            root = root.children[c]
        return pref

