import collections

def calculate_sum_of_colors(n, node_colors, edges):

    def bfs_sum(i):
        value = 1
        seen = {i}
        q = collections.deque([(i, {node_colors[i]})])
        while q:
            (t, colors) = q.popleft()
            value += len(colors)
            for edge in edges[t]:
                if edge not in seen:
                    seen.add(edge)
                    q.append((edge, colors | {node_colors[edge]}))
        return value
    results = []
    for i in range(n):
        results.append(bfs_sum(i))
    return results

