def minimize_total_cost(n, a, b):
    dp = 1
    ans = 0
    total = 0
    for (i, j) in zip(a, b):
        dp = dp << i | dp << j
        total += i + j
        ans += i * i + j * j
    i = total // 2
    dp >>= i
    while dp > 0 and i <= total:
        if dp & 1:
            return ans * (n - 2) + i * i + (total - i) ** 2
        dp >>= 1
        i += 1
    return ans * (n - 2) + i * i + (total - i + 2)

