def determine_game_winners(T, test_cases):

    def hackenbush(i, p, g):
        v = 1
        for x in g[i]:
            if x == p:
                continue
            v = v ^ hackenbush(x, i, g) + 1
        return v
    results = []
    for case in test_cases:
        (N, edges) = case
        g = {i: [] for i in range(1, N + 1)}
        for (a, b) in edges:
            g[a].append(b)
            g[b].append(a)
        if hackenbush(1, 0, g):
            results.append('Alice')
        else:
            results.append('Bob')
    return results

