    if n == 1:
        return (arr[0], arr[0])
    if n == 2 and arr[0] < 1 and (arr[1] == 0):
        return (arr[0], arr[1])
    k = 1
    lis = []
    maxa = 0
    for i in range(n):
        if arr[i] != 0:
            k *= arr[i]
        if arr[i] < 0:
            lis.append(arr[i])
    if len(lis) == 0:
        maxa = k
        mini = min(arr)
    elif len(lis) % 2 == 0:
        maxa = k
        mini = k // max(lis)
    elif len(lis) % 2 != 0:
        maxa = k // max(lis)
        mini = k
    return (mini, maxa)

