import sys
from bisect import bisect_right

def restore_segment_tree(n, shuffled_values):
    nl = 1
    x = n
    while x:
        nl += 1
        x >>= 1
    occ = {}
    for e in shuffled_values:
        if e not in occ:
            occ[e] = 0
        occ[e] += 1
    cnt_occ = {}
    for v in occ.values():
        if v not in cnt_occ:
            cnt_occ[v] = 0
        cnt_occ[v] += 1
    for i in range(1, nl):
        if i not in cnt_occ or cnt_occ[i] != 1 << nl - 1 - i:
            return ('NO', [])
    ah = [[] for _ in range(nl + 2)]
    for (i, v) in occ.items():
        ah[v].append(i)
    sofar = ah[nl]
    res = list(sofar)
    for i in range(1, nl):
        sofar = z(sofar, ah[nl - i])
        res += sofar
    return ('YES', res)

def z(a, b):
    B = sorted(b)
    r = []
    for i in range(len(a)):
        ind = bisect_right(B, a[i])
        r += [a[i], B[ind]]
        del B[ind]
    return r

