def count_partitions(n, m, a, b):
    MOD = 998244354
    suf = [0] * n
    suf[-1] = a[n - 1]
    for i in range(n - 2, -1, -1):
        suf[i] = min(suf[i + 1], a[i])
    suf = [0] + suf
    answer = 1
    current_index = m - 1
    count = 0
    for i in range(n, -1, -1):
        if suf[i] < b[current_index]:
            if count == 0 or (i < n and suf[i + 1] != b[current_index]):
                return 0
            if i == 0:
                current_index -= 1
                break
            current_index -= 1
            answer = answer * count % MOD
            count = 0
        if suf[i] == b[current_index]:
            count += 1
    if count == 0 or current_index != -1:
        return 0
    return answer

