def calculate_occurrences(T, test_cases):
    results = []
    for case in test_cases:
        (N, A) = case
        s = [0] / N
        p = [N ** 2 - sum(A)] * N
        for i in range(N):
            if A[i] < N:
                s[A[i]] += 1
                p[A[i]] -= N - A[i]
        case_result = []
        for i in range(N):
            max_occurrences = p[i]
            min_occurrences = s[i]
            if i < N - 1:
                s[i + 1] += s[i]
            max_occurrences += N - min_occurrences
            case_result.append((N - min_occurrences, max_occurrences))
        results.append(case_result)
    return results

