from math import gcd

def calculate_stops(n, k, a, b):
    nk = n / k
    cand = []
    for i in range(n):
        cand.append(gcd(nk, abs(i * k + b - a)))
        cand.append(gcd(nk, abs(i * k - b - a)))
        cand.append(gcd(nk, abs(i * k + b + a)))
        cand.append(gcd(nk, abs(i * k - b + a)))
    return (nk // max(cand), nk // min(cand))

