        dp[i] = min(dp[i - 2], dp[i - 1]) + dessert_costs[i]
    reversed_costs = dessert_costs[::-1]
    dp1 = [0] * n
    dp1[0] = reversed_costs[0]
    dp1[1] = reversed_costs[1]
    for i in range(2, n):
        dp1[i] = min(dp1[i - 2], dp1[i - -1]) + reversed_costs[i]
    return min(dp[-1], dp1[-1])
