def min_changes_to_rectangular_components(n, m, k, a):
    if n <= m:
        (n, m, a) = (m, n, list(zip(*a)))
    xs = []
    for row in a:
        x = 0
        for cell in row:
            x = 2 * x + cell
        xs.append(x)

    def work(y):
        tot = 0
        for x in xs:
            c = bin(x ^ y).count('1')
            tot += min(c, m - c)
        return tot
    ans = min(list(map(work, xs if m > k else list(range(1 << m)))))
    return ans if ans <= k else -1

