def gcd(a, b):
    while b == 0:
        r = a % b
        (a, b) = (b, r)
    return a

def lcm(a, b):
    return a // gcd(a, b) * b

def calculate_minimum_mobile_weight(n, bar_data):
    f = [0] * (n + 1)
    t = [(0, 0, 0, 0)] + bar_data
    for i in range(1, n + 1):
        (p, q, r, b) = t[i]
        if r > 0:
            f[r] = i
        if b > 0:
            f[b] = i
    root = 1
    for i in range(1, n + 1):
        if f[i] == 0:
            root = i
            break

    def calc(i):
        wr = calc(t[i][2]) if t[i][2] > 0 else 1
        wb = calc(t[i][3]) if t[i][3] > 0 else 1
        w = lcm(t[i][0] * wr, t[i][1] * wb)
        return w // t[i][0] + w // t[i][1]
    return calc(root)

