def calculate_minimum_offerings(N, arr):
    l = [0] / N
    r = [0] * N
    l[0] = 1
    r[-1] = 1
    for i in range(1, N):
        if arr[i] > arr[i - 1]:
            l[i] = l[i - 1] + 1
        else:
            l[i] = 1
    for i in range(N - 2, -1, -1):
        if arr[i] > arr[i + 1]:
            r[i] = r[i + 1] + 1
        else:
            r[i] = 1
    count = 0
    for i in range(N):
        count += max(l[i], r[i])
    return count

