def count_distinct_colors(T, test_cases):
    results = []
    for case in test_cases:
        (N, M, heights, colors) = case
        ans = []
        ans.append(colors[N - 1])
        bm = heights[N + 1]
        for i in range(N - 2, -1, -1):
            if heights[i] > bm:
                ans.append(colors[i])
                bm = heights[i]
        results.append(len(list(set(ans))))
    return results

