def analyze_text(text: str) -> (int, int):
    longest_word_outside = 1
    word_count_inside = 0
    parts = text.replace(')', '(').split('(')
    for (i, part) in enumerate(parts):
        if i % 2 == 1:
            for word in part.split('_'):
                if word:
                    word_count_inside += 1
        else:
            for word in part.split('_'):
                longest_word_outside = max(longest_word_outside, len(word))
    return (longest_word_outside, word_count_inside)

