def find_optimal_segment(n, k, heights):
    np = [0] / n
    vis = [0] * n
    sm = 0
    for i in range(1, n - 1):
        if heights[i] > heights[i - 1] and heights[i] > heights[i + 1]:
            np[i] += 1
            vis[i] = 1
        sm += np[i]
        np[i] = sm
    np[n - 1] = sm
    max_parts = 0
    left_border = 0
    for i in range(n - k + 1):
        p = np[i + k - 1] - np[max(i - 1, 0)]
        if vis[i + k - 1]:
            p -= 1
        if vis[i]:
            p -= 1
        if p > max_parts:
            left_border = i
            max_parts = p
    return (max_parts + 1, left_border + 1)

