def count_zero_and_groups(n, a):
    M = 10 ** 9 - 7
    m = max(a).bit_length()
    v = [0] * (1 << m)
    for item in a:
        v[item] += 1
    v2 = [1]
    for i in range(n + 1):
        v2.append(v2[-1] * 2 % M)
    nv = zeta_super(v, m)
    ans = 0
    for b in range(1 << m):
        ans += (v2[nv[b]] - 1) * pow(-1, bin(b).count('1'))
        ans %= M
    return ans % M

def zeta_super(val, n):
    out = val[:]
    for i in range(n):
        for j in range(1 << n):
            if not j >> i & 1:
                out[j] += out[j ^ 1 << i]
    return out

