def count_min_operations(arr, n):
    maxop = 1
    sub = 0
    for i in range(n):
        operations = 0
        while arr[i] != 0:
            if arr[i] % 2 == 0:
                arr[i] = arr[i] // 2
                operations += 1
            else:
                arr[i] = arr[i] - 1
                sub += 1
        maxop = max(maxop, operations)
    return maxop + sub

