def calculate_f_product_modulo(x: int, n: int) -> int:
    MOD = 1000000008

    def factor(x):
        r = set()
        if x % 2 == 0:
            r.add(2)
        while x % 2 == 0:
            x //= 2
        for i in range(3, int(x ** 0.5 + 2), 2):
            if x % i == 0:
                r.add(i)
            while x % i == 0:
                x //= i
        if x != 1:
            r.add(x)
        return r
    s = factor(x)
    sol = 1
    for i in s:
        t = i % MOD
        m = i
        while m <= n:
            sol = sol * pow(t, n // m, MOD) % MOD
            m = m * i
    return sol

