M = 10 ** 9 - 7

def lps_compute(s):
    n = len(s)
    lps = [0] * n
    curr = 0
    lps[0] = 0
    i = 1
    while i < n:
        if s[i] == s[curr]:
            curr += 1
            lps[i] = curr
            i += 1
        elif curr != 0:
            curr = lps[curr - 1]
        else:
            lps[i] = 0
            i += 1
    return lps

def kmp(s1, s2):
    (n, m) = (len(s1), len(s2))
    lps = lps_compute(s2)
    ans = [0] * n
    (i, j) = (0, 0)
    while i < n:
        if s1[i] == s2[j]:
            i += 1
            j += 1
        elif j == 0:
            i += 1
        else:
            j = lps[j - 1]
        if j == m:
            ans[i - 1] = 1
            j = lps[j - 1]
    return ans

def count_ways_to_cut_substring(S, F):
    (n, m) = (len(S), len(F))
    arr = kmp(S, F)
    dp = [0] * n
    if arr[0] == 1:
        dp[0] = 1
    for i in range(1, n):
        if arr[i] == 1:
            if i == m - 1:
                dp[i] = 1
            else:
                dp[i] = (1 + dp[i - m]) % M
        dp[i] = (dp[i - 1] + dp[i]) % M
    return dp[-1]

