def find_vertex_colors(N, M, edges, Q, operations):
    INF = 10 * 14
    graph = [[] for _ in range(N)]
    for (a, b) in edges:
        graph[a - 1].append(b - 1)
        graph[b - 1].append(a - 1)
    maxd = 0
    Query = []
    Color = []
    for (v, d, c) in operations:
        if d > maxd:
            maxd = d
        Query.append((v - 1, d))
        Color.append(c)
    Query = Query[::-1]
    Color = Color[::-1]
    dp = [[INF] * (maxd + 1) for _ in range(N)]
    for (i, (v, d)) in enumerate(Query):
        if i < dp[v][d]:
            dp[v][d] = i
    for d in reversed(range(maxd)):
        for v in range(N):
            if dp[v][d + 1] < dp[v][d]:
                dp[v][d] = dp[v][d + 1]
            for nv in graph[v]:
                if dp[v][d + 1] < dp[nv][d]:
                    dp[nv][d] = dp[v][d + 1]
    colors = []
    for v in range(N):
        seq = dp[v][0]
        if seq == INF:
            colors.append(0)
        else:
            colors.append(Color[seq])
    return colors

